import axios from "axios";
import { setAlert } from "./alert";

import {
  GET_PROFILE,
  PROFILE_ERROR,
  UPDATE_PROFILE,
  CLEAR_PROFILE,
  ACCOUNT_DELETED,
  GET_PROFILES,
  GET_EXPERIENCE,
  GET_EDUCATION,
  GET_REPOS,
  POST_EXPERIENCE,
  ACCOUNT_DELETED2,
  CLEAR_USERS,
  GET_USERS,
  USERS_ERROR,
  POST_EDUCATION,
  ALL_USERS,
  // POST_FILE,
} from "./types";

export const getCurrentProfile = () => async (dispatch) => {
  try {
    const res = await axios.get("/api/profile/me");

    dispatch({
      type: GET_PROFILE,
      payload: res.data,
    });
  } catch (err) {
    dispatch({ type: CLEAR_PROFILE });
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};

export const getCurrentExperience = (ExpId) => async (dispatch) => {
  try {
    const res = await axios.get(`/api/profile/experience/${ExpId}`);

    dispatch({
      type: GET_EXPERIENCE,
      payload: res.data,
    });
  } catch (err) {
    dispatch({ type: CLEAR_PROFILE });
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};

export const setCurrentExperience = (ExpId, formData) => async (dispatch) => {
  const config = {
    headers: {
      "Content-Type": "application/json",
    },
  };
  try {
    const res = await axios.post(
      `/api/profile/experience/${ExpId}`,
      formData,
      config
    );

    dispatch({
      type: POST_EXPERIENCE,
      payload: res.data,
    });
  } catch (err) {
    dispatch({ type: CLEAR_PROFILE });
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};

export const getCurrentEducation = (EduId) => async (dispatch) => {
  try {
    const res = await axios.get(`/api/profile/education/${EduId}`);

    dispatch({
      type: GET_EDUCATION,
      payload: res.data,
    });
  } catch (err) {
    dispatch({ type: CLEAR_PROFILE });
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};

export const setCurrentEducation = (EduId, formData) => async (dispatch) => {
  const config = {
    headers: {
      "Content-Type": "application/json",
    },
  };
  try {
    const res = await axios.post(
      `/api/profile/education/${EduId}`,
      formData,
      config
    );
    dispatch({
      type: POST_EDUCATION,
      payload: res.data,
    });
  } catch (err) {
    dispatch({ type: CLEAR_PROFILE });
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};

export const getProfiles = () => async (dispatch) => {
  dispatch({ type: CLEAR_PROFILE });
  try {
    const res = await axios.get("/api/profile");

    dispatch({
      type: GET_PROFILES,
      payload: res.data,
    });
  } catch (err) {
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.statusText, status: err.status },
    });
  }
};

export const getUsers = () => async (dispatch) => {
  dispatch({ type: CLEAR_USERS });
  try {
    const res = await axios.get("/api/profile/getusers");

    dispatch({
      type: GET_USERS,
      payload: res.data,
    });
  } catch (err) {
    dispatch({
      type: USERS_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};

export const getAllusers = () => async(dispatch) =>{
  dispatch({ type: CLEAR_USERS });
